import React, { useRef } from "react";
import MealCart from "../UI/MealCart";
const NewMealItem = () => {
  const name = useRef();
  const description = useRef();
  const price = useRef();

  const submitHandler = async (event) => {
    event.preventDefault();

    const response = await fetch(
      "https://food-order-app-4dedb-default-rtdb.firebaseio.com/meals.json",
      {
        method: "POST",
        headers: "application/json",
        body: JSON.stringify({ name, description, price }),
      }
    );
    const console = await response.json();
    console.log(console);
  };
  return (
    <MealCart>
      <form onSubmit={submitHandler}>
        <label htmlFor="name">Name</label>
        <input id="name" type="text" ref={name} />
        <label htmlFor="description">Description</label>
        <input id="description" type="text" ref={description} />
        <label htmlFor="price">Price</label>
        <input id="price" type="numbers" ref={price} />
        <button type="submit">Add Meal</button>
      </form>
    </MealCart>
  );
};

export default NewMealItem;
