import React, { useEffect, useState } from "react";
import classes from "./MealsAvailable.module.css";
import MealCart from "../UI/MealCart";
import MealItem from "./MealItem/MealItem";

const MealsAvailable = (props) => {
  const [meals, setMeals] = useState([]);
  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch(
        "https://food-order-app-4dedb-default-rtdb.firebaseio.com/meals.json"
      );
      const data = await response.json();

      console.log(data);
      let uniqueChars = [];
      if (props.refresh) {
        return setMeals((oldArray) => [
          ...new Set(oldArray),
          {
            name: props.newMeal.name,
            description: props.newMeal.description,
            price: props.newMeal.price,
          },
        ]);
      }
      for (let key in data) {
        setMeals((oldArray) => [
          ...oldArray,

          {
            name: data[key].name,
            description: data[key].description,
            price: data[key].price,
          },
        ]);
      }
    };

    fetchData();
  }, [props.refresh]);

  return (
    <MealCart className={classes.meals}>
      <ul>
        {meals.map((meal) => (
          <MealItem key={meal.name} meal={meal} id={meal.id} />
        ))}
      </ul>
    </MealCart>
  );
};

export default MealsAvailable;
