import React, { useRef, useContext, useState } from "react";
import classes from "./WithoutLogin.module.css";
import CartContext from "../../store/cart-context";
const WithoutLogin = (props) => {
  const cartItems = useContext(CartContext);

  const first_name = useRef();
  const last_name = useRef();
  const city = useRef();
  const full_address = useRef();

  const inputValidate = (val) => {
    if (val) {
      return val.current.value.trim().length > 0 ? true : false;
    }

    return false;
  };
  const nameValidate = !inputValidate(first_name);
  const lnameValidate = !inputValidate(last_name);
  const cityValidate = !inputValidate(city);
  const addressValidate = !inputValidate(full_address);
  const confirmHandler = async (event) => {
    const isFormValid =
      nameValidate && lnameValidate && cityValidate && addressValidate;

    if (!isFormValid) {
      return;
    }
    const user_info = {
      first_name: first_name.current.value,
      last_name: last_name.current.value,
      city: city.current.value,
      full_address: full_address.current.value,
    };

    let order_info = [];
    for (let items of cartItems.items) {
      order_info.push(items);
    }
    event.preventDefault();
    await fetch(
      "https://food-order-app-4dedb-default-rtdb.firebaseio.com/orders.json",
      {
        method: "POST",
        body: JSON.stringify({
          customer_info: user_info,
          order_info: order_info,
          charge: cartItems.totalAmount.toFixed(2),
        }),
        headers: { "Content-type": "application/json" },
      }
    )
      .then(props.onCancel)
      .catch((err) => console.log(err.message));
  };
  return (
    <form onSubmit={confirmHandler}>
      <p className={classes.paragraph}>Order Informations</p>
      <div className={classes.inputs}>
        <div>
          {" "}
          <label htmlFor="name">Name </label>
          <input
            type="text"
            id="name"
            name="name"
            className={classes["input--shape"]}
            ref={first_name}
          />
          {nameValidate && <p>Please enter a valid name.</p>}
        </div>
        <div>
          {" "}
          <label htmlFor="lastname">Last Name </label>
          <input
            type="text"
            id="lastname"
            name="lastname"
            className={classes["input--shape"]}
            ref={last_name}
          />
        </div>
      </div>

      <div className={classes.inputs}>
        <div>
          <div>
            <label htmlFor="city">City </label>
            <select
              name="city"
              id="city"
              className={classes["input--shape"]}
              ref={city}
            >
              <option value="">Choose...</option>
              <option value="1">Adana</option>
              <option value="2">Adıyaman</option>
              <option value="3">Afyonkarahisar</option>
              <option value="4">Ağrı</option>
              <option value="5">Amasya</option>
              <option value="6">Ankara</option>
              <option value="7">Antalya</option>
              <option value="8">Artvin</option>
              <option value="9">Aydın</option>
              <option value="10">Balıkesir</option>
              <option value="11">Bilecik</option>
              <option value="12">Bingöl</option>
              <option value="13">Bitlis</option>
              <option value="14">Bolu</option>
              <option value="15">Burdur</option>
              <option value="16">Bursa</option>
              <option value="17">Çanakkale</option>
              <option value="18">Çankırı</option>
              <option value="19">Çorum</option>
              <option value="20">Denizli</option>
              <option value="21">Diyarbakır</option>
              <option value="22">Edirne</option>
              <option value="23">Elazığ</option>
              <option value="24">Erzincan</option>
              <option value="25">Erzurum</option>
              <option value="26">Eskişehir</option>
              <option value="27">Gaziantep</option>
              <option value="28">Giresun</option>
              <option value="29">Gümüşhane</option>
              <option value="30">Hakkâri</option>
              <option value="31">Hatay</option>
              <option value="32">Isparta</option>
              <option value="33">Mersin</option>
              <option value="34">İstanbul</option>
              <option value="35">İzmir</option>
              <option value="36">Kars</option>
              <option value="37">Kastamonu</option>
              <option value="38">Kayseri</option>
              <option value="39">Kırklareli</option>
              <option value="40">Kırşehir</option>
              <option value="41">Kocaeli</option>
              <option value="42">Konya</option>
              <option value="43">Kütahya</option>
              <option value="44">Malatya</option>
              <option value="45">Manisa</option>
              <option value="46">Kahramanmaraş</option>
              <option value="47">Mardin</option>
              <option value="48">Muğla</option>
              <option value="49">Muş</option>
              <option value="50">Nevşehir</option>
              <option value="51">Niğde</option>
              <option value="52">Ordu</option>
              <option value="53">Rize</option>
              <option value="54">Sakarya</option>
              <option value="55">Samsun</option>
              <option value="56">Siirt</option>
              <option value="57">Sinop</option>
              <option value="58">Sivas</option>
              <option value="59">Tekirdağ</option>
              <option value="60">Tokat</option>
              <option value="61">Trabzon</option>
              <option value="62">Tunceli</option>
              <option value="63">Şanlıurfa</option>
              <option value="64">Uşak</option>
              <option value="65">Van</option>
              <option value="66">Yozgat</option>
              <option value="67">Zonguldak</option>
              <option value="68">Aksaray</option>
              <option value="69">Bayburt</option>
              <option value="70">Karaman</option>
              <option value="71">Kırıkkale</option>
              <option value="72">Batman</option>
              <option value="73">Şırnak</option>
              <option value="74">Bartın</option>
              <option value="75">Ardahan</option>
              <option value="76">Iğdır</option>
              <option value="77">Yalova</option>
              <option value="78">Karabük</option>
              <option value="79">Kilis</option>
              <option value="80">Osmaniye</option>
              <option value="81">Düzce</option>
            </select>
          </div>
        </div>
        <div className={classes.textarea}>
          <label htmlFor="address" className={classes["textarea--label"]}>
            Full Address{" "}
          </label>
          <textarea
            id="address"
            name="address"
            rows="5"
            cols="30"
            placeholder="Enter Full Address"
            className={classes["input--shape"]}
            ref={full_address}
          />
        </div>
      </div>
      <div className={classes.buttons}>
        <button
          type="button"
          onClick={props.onCancel}
          className={classes["order--cancel"]}
        >
          Cancel
        </button>
        <button className={classes["order--btn"]}>Confirm</button>
      </div>
    </form>
  );
};

export default WithoutLogin;
