import React, { useRef } from "react";
import MealCart from "../UI/MealCart";
import classes from "./NewMealItem.module.css";
const NewMealItem = (props) => {
  const name = useRef();
  const description = useRef();
  const price = useRef();

  const submitHandler = async (event) => {
    event.preventDefault();
    const data = {
      name: name.current.value,
      description: description.current.value,
      price: +price.current.value,
    };
    let validateData =
      name.current.value.trim().length > 0 &&
      description.current.value.trim().length > 0 &&
      price.current.value.trim().length > 0;
    if (validateData) {
      const response = await fetch(
        "https://food-order-app-4dedb-default-rtdb.firebaseio.com/meals.json",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(data),
        }
      );
      const see = await response.json();
      console.log(see);
      return props.handleRefresh(true);
    }
    props.handleRefresh(false);
  };
  return (
    <MealCart className={classes.new}>
      <form onSubmit={submitHandler}>
        <label htmlFor="name">Name: </label>
        <input id="name" type="text" ref={name} />

        <label htmlFor="description">Description: </label>
        <input id="description" type="text" ref={description} />

        <label htmlFor="price">Price: </label>
        <input id="price" type="numbers" ref={price} />

        <button type="submit">Add Meal</button>
      </form>
    </MealCart>
  );
};

export default NewMealItem;
